import { createStore } from 'vuex'
import axios from "axios";

const store = createStore({

  state: {
    
  },
  getters: {
    
  },
  actions: {
    
  },
  mutations: {
    auth_request(state) {
      state.status = 'loading'
    },
    auth_success(state, token) {
      state.status = 'success'
      state.token = token
    },
    set_user(state, user) {
      state.user = user
    },
    handle_error(state, error) {
      state.error = error
    },
  }
});

export default store;
